import zipfilefrom excel_scraper import excel_scrapertable1_section = [    'OECD DEMAND',    'NON-OECD DEMAND',    'OECD SUPPLY',    'NON-OECD SUPPLY',    'OPEC',    'STOCK CHANGES AND MISCELLANEOUS',    'Memo items:']def read_world_snd_table1(omr_zip_loc):    z = zipfile.ZipFile(omr_zip_loc)    df = excel_scraper.read_table(z.open('August_2021_Table1.xls'), skiprows=[0, 1, 2, 3, 4, 5, 6])    df['Prefix'] = df['Unnamed: 0']    df.loc[~df['Prefix'].isin(table1_section), 'Prefix'] = None    df['Prefix'] = df['Prefix'].fillna(method='ffill')    df['Concat'] = df.apply(lambda x: '%s-%s' % (x['Prefix'], x['Unnamed: 0']), 1)    df = df.set_index('Concat')    df = df.filter(regex='\d\d\d\d|\d[Q]\d\d', axis=1)  # filter only on valid dates    df = df.T    df = df.dropna(how='all', axis=1)    return df