import syssys.path.append('../')import pandas as pdfrom oilanalytics.energyaspects import energyaspects_apifrom oilanalytics.balances import balance_utils as bufrom datetime import datetimeimport importlibimport reimport xlsxwriterdef generate_product_page(commod, writer):    column_format = {'format': ('extract_region', 'aspect', 'source'), 'multiindex': True}    dfw = energyaspects_api.read_commod_balance(commod='gasoline', geography='WORLD', column_format=column_format)    dfw.T.to_excel(writer, sheet_name=commod, startcol=0)def generate_balance():    ts = datetime.now().strftime('%Y%m%d_%H%Y%M')    workbookname = f'balance_{ts}.xlsx'    writer = pd.ExcelWriter(workbookname, engine='xlsxwriter')    for commod in ['gasoline', 'diesel']:        generate_product_page(commod, writer)    # # headers    # for col_num, data in enumerate(quarters):    # 	worksheet.write(row, col_num + col, data, yearly_headers)    writer.save()    return workbooknameif __name__ == '__main__':    generate_balance()