from oilanalytics.freight.tce import tce_consts as constimport pandas as pdimport cachetools.funcfrom commodutil import pandasutil as pdu## TCE Calculation Notes## https://www.balticexchange.com/content/balticexchange/consumer/en/my-baltic/downloads.html# Note: add +5 for bunker premiumdef china_eca_fuel_history(row, voyage_days):    if row.name.year >= 2020: # IMO        return (row.VLSFO + 5) * voyage_days['voyage_noneca'] + (row.MGO + 5) * voyage_days['voyage_eca']    if row.name.year >= 2019: # Pre-IMO, China ECA        return (row.HSFO + 5) * voyage_days['voyage_noneca'] + (row.MGO + 5) * voyage_days['voyage_eca']    else:        return (row.HSFO + 5) * (voyage_days['voyage_noneca'] + voyage_days['voyage_eca'])def eca_fuel_history(row, voyage_days):    if row.name.year >= 2020: # IMO        return (row.VLSFO + 5) * voyage_days['voyage_noneca'] + (row.MGO + 5) * voyage_days['voyage_eca']    if row.name.year >= 2014: # Pre-IMO        return (row.HSFO + 5) * voyage_days['voyage_noneca'] + (row.MGO + 5) * voyage_days['voyage_eca']    if row.name >= pd.to_datetime('2010-07-06'): # ECA 1%        return (row.HSFO + 5) * voyage_days['voyage_noneca'] + (row.LSFO + 5) * voyage_days['voyage_eca']    else:        return row.HSFO * (voyage_days['voyage_noneca'] + voyage_days['voyage_eca'])def non_eca_fuel_history(row, voyage_days):    if row.name.year >= 2020: # IMO        return (row.VLSFO + 5) * voyage_days['voyage_noneca']    if row.name.year >= 2014: # Pre-IMO        return (row.HSFO + 5) * voyage_days['voyage_noneca']def calc(arb, data):    data['Freight_USDMT'] = (data.FlatRate/100) * data.WorldScale    data['GrossFreight'] = const.dwt[arb] * data['Freight_USDMT']    # GRT Rotterdam Port dues    if arb in ['tc2', 'td20']:        if 'Rotterdam_Port_Dues_Product_A' not in data.columns:            rott = get_rott_port_dues()            if pd.infer_freq(data.index) == 'MS':                data['Rotterdam_Port_Dues_Product_A'] = rott.iloc[-1]['Rotterdam_Port_Dues_Product_A']                data['Rotterdam_Port_Dues_Product_B'] = rott.iloc[-1]['Rotterdam_Port_Dues_Product_B']            else:                data = pdu.mergets(data, rott)        data['GrossFreight'] = data['GrossFreight'] + ((data.Rotterdam_Port_Dues_Product_A + (data.Rotterdam_Port_Dues_Product_A * 1.33639)) * const.grt[arb])    if arb in ['tc5']:        data['GrossFreight'] = data['GrossFreight'] + (const.dwt[arb] * const.rastanura_prem)    if arb in ['tc12']:        data['GrossFreight'] = data['GrossFreight'] + (const.dwt[arb] * 0.24)    # Bunker costs    if arb in const.arbs_using_sing_fuels: # convert Sing MGO to bbl        data['MGO'] = data['MGO'] * 7.45    if arb in ['tc12']:        data['BunkerCost'] = data.apply(lambda x: non_eca_fuel_history(x, const.voyage_days[arb]), 1)    if arb in ['tc5']:        data['BunkerCost'] = data.apply(lambda x: china_eca_fuel_history(x, const.voyage_days[arb]), 1)    else:        data['BunkerCost'] = data.apply(lambda x: eca_fuel_history(x, const.voyage_days[arb]), 1)    # Net Freight    data['NetFreight'] = data['GrossFreight'] * (1 - const.commission[arb])    # TCE    data['NetIncome'] = data.NetFreight - (data.BunkerCost + data.PortCosts)    data['TCE'] = data.NetIncome / const.voyage_days[arb]['voyage_days']    return data@cachetools.func.ttl_cache(ttl=100 * 60)def get_rott_port_dues(resample_daily=True):    url = 'https://raw.githubusercontent.com/aeorxc/oilanalytics/main/oilanalytics/freight/tce/rott_port_dues.csv'    df = pd.read_csv(url, index_col=0, parse_dates=True)    if resample_daily:        df = df.resample('B').mean().fillna(method='ffill')    return df