from pylim import limimport plotly.graph_objects as gofrom commodplot import commodplotutil as cpufutures_dict = {        'FB': 'Brent',        'CL': 'WTI',        'DME.OQ': 'Dubai',        'ICE.ADM': 'Murban',        'RB.UNL': 'RBOB',        'HO': 'HO',        'FP': 'ULSD'    }crude_futures = {'FB': 'Brent', 'CL': 'WTI', 'DME.OQ': 'Dubai'}def candle_chart(symbol, days=90, title=None):    q = 'Show Close: Close of %s High: High of %s Low: Low of %s Open: Open of %s when date is within %s days' % (symbol, symbol, symbol, symbol, days)    df = lim.query(q)    fig = go.Figure(data=[go.Candlestick(x=df.index,                                         open=df['Open'],                                         high=df['High'],                                         low=df['Low'],                                         close=df['Close'])])    title = '%s %s' % (futures_dict.get(symbol, title), cpu.gen_title(df))    fig.update_layout(title=title)    return figdef get_prices(futures, curve = False, start_date=None):    if curve:        df = lim.curve(futures)    else:        df = lim.series(futures, start_date=start_date)    return dfif __name__ == '__main__':    print(get_prices(crude_futures))