from energyaspects import energyaspectsdef test_get_all_timeseries_def():    res = energyaspects.get_timeseries_def()    assert 1 in res.indexdef test_read_timeseries():    res = energyaspects.read_timeseries((1530,1531))    assert 1530 in res.columns    assert 1531 in res.columnsdef test_detailed_metadata():    res = energyaspects.detailed_metadata(4020)    assert res.source.iloc[0] == 'Energy Aspects'