import pandas as pdfrom oilanalytics.prices import futures_pricesdef test_get_prices_hist():    res = futures_prices.get_prices(futures_prices.futures_dict)    assert isinstance(res, pd.DataFrame)def test_get_prices_fwd():    res = futures_prices.get_prices(futures_prices.futures_dict, curve=True)    assert isinstance(res, pd.DataFrame)def test_candle_chart():    res = futures_prices.candle_chart('FB', days=10)    assert isinstance(res, futures_prices.go.Figure)