import pandas as pdimport pytestfrom oilanalytics.freight import tce_calc# def test_tc2():#     dd = {#         'FlatRate': 14.73,#         'PortCosts': 91276.748,#         'WorldScale': 80.56,#         'Rotterdam_Port_Dues_Product_A': 0.35,#         'Rotterdam_Port_Dues_Product_B': 0.57,#         'VLSFO': 276.75,#         'HSFO': 227.5,#         'LSFO': 243.5,#         'MGO': 339.5,#     }#     data = pd.DataFrame(dd, index=[pd.to_datetime('2020-06-25')])#     res = tce_calc.calc('tc2', data)##     assert res['GrossFreight']['2020-06-25'] == pytest.approx(469632.97)#     assert res['BunkerCost']['2020-06-25'] == pytest.approx(200407.9, 0.1)#     assert res['TCE']['2020-06-25'] == pytest.approx(5728.616354, 0.1)def test_tc5():    ds = '2021-10-06'    dd = {        'FlatRate': 20.5,        'WorldScale': 103.14,        'MGO': 355.001,        'VLSFO': 577.27,        'HSFO': 225.79,        'LoadPortCosts': 21181.07,        'DischargePortCosts': 54347.78,    }    data = pd.DataFrame(dd, index=[pd.to_datetime(ds)])    res = tce_calc.calc('tc5', data)    assert res['GrossFreight'][ds] == pytest.approx(1177753.5, abs=1e-1)    assert res['BunkerCost'][ds] == pytest.approx(853965.2778, abs=1e-1)    assert res['TCE'][ds] == pytest.approx(4036.00, abs=1e-1)# def test_tc6():#     dd = {#         'MGO': 57.88114,#         'VLSFO': 414.6,#         'HSFO': 317.35,#         'PortCosts': 120597.3,#         'FlatRate': 6.510000000000001,#         'WorldScale': 91.19}##     data = pd.DataFrame(dd, index=[pd.to_datetime('2020-06-25')])#     res = tce_calc.calc('tc6', data)##     assert res['GrossFreight']['2020-06-25'] == pytest.approx(178094.07, 0.1)#     assert res['BunkerCost']['2020-06-25'] == pytest.approx(707936.73, 0.1)#     assert res['TCE']['2020-06-25'] == pytest.approx(-84345.44, 0.1)# def test_tc7():#     dd = {#         'MGO': 431.21450000000004,#         'VLSFO': 414.6,#         'HSFO': 317.35,#         '